import React, { useEffect, useState } from 'react';
import Header from './components/Header';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import Dashboard from './components/Dashboard';
import UserProfile from "./components/UserProfile";
import { getTasks, createTask, updateTask, deleteTask } from './api';
import Confetti from 'react-confetti';
import { Chart, ArcElement, Tooltip, Legend } from 'chart.js';
import './App.css';

Chart.register(ArcElement, Tooltip, Legend);

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [editing, setEditing] = useState(null);
  const [coins, setCoins] = useState(0);
  const [showConfetti, setShowConfetti] = useState(false);
  const [message, setMessage] = useState('');
  const [username, setUsername] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Fetch tasks only when logged in
  const fetchTasks = async () => {
    try {
      const res = await getTasks();
      setTasks(res.data);
      const completed = res.data.filter((t) => t.completed).length;
      setCoins(completed * 10);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    if (isLoggedIn) fetchTasks();
  }, [isLoggedIn]);

  // Task handlers
  const handleCreate = async (payload) => {
    try {
      await createTask(payload);
      fetchTasks();
    } catch (err) {
      console.error(err);
    }
  };

  const handleUpdate = async (id, payload) => {
    try {
      await updateTask(id, payload);
      setEditing(null);
      fetchTasks();
    } catch (err) {
      console.error(err);
    }
  };

  const handleDelete = async (task) => {
    if (!window.confirm(`Delete task "${task.title}"?`)) return;
    try {
      await deleteTask(task._id);
      fetchTasks();
    } catch (err) {
      console.error(err);
    }
  };

  const handleToggleComplete = async (task) => {
    const updated = { ...task, completed: !task.completed };
    await handleUpdate(task._id, updated);

    if (updated.completed) {
      setShowConfetti(true);
      setMessage(`ðŸŽ‰ Congratulations! You earned 10 coins for completing "${task.title}"!`);
      setTimeout(() => {
        setShowConfetti(false);
        setMessage('');
      }, 3000);
    }
  };

  // UserProfile handlers
  const handleEditProfile = () => {
    const newName = prompt("Enter your new username:", username);
    if (newName) setUsername(newName);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername("");
    setCoins(0);
    setTasks([]);
  };

  // Login handler
  const handleLogin = (name) => {
    if (name.trim() === "") return;
    setUsername(name);
    setIsLoggedIn(true);
  };

  // Chart data
  const completedCount = tasks.filter((t) => t.completed).length;
  const pendingCount = tasks.length - completedCount;

  // Render login screen
  if (!isLoggedIn) {
    return (
      <div className="login-container">
        <h2>Login</h2>
        <input
          type="text"
          placeholder="Enter username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <button onClick={() => handleLogin(username)} disabled={!username.trim()}>
          Login
        </button>
      </div>
    );
  }

  // Main app
  return (
    <div className="container">
      {showConfetti && <Confetti />}
      <Header coins={coins} />
      {message && <div className="motivation">{message}</div>}

      <main>
        <div className="left">
          <UserProfile
            username={username}
            coins={coins}
            onEditProfile={handleEditProfile}
            onLogout={handleLogout}
          />

          <TaskForm
            onCreate={handleCreate}
            onUpdate={handleUpdate}
            editing={editing}
            cancelEdit={() => setEditing(null)}
          />

          <Dashboard tasks={tasks} />
        </div>

        <div className="right">
          <TaskList
            tasks={tasks}
            onEdit={(t) => setEditing(t)}
            onDelete={handleDelete}
            onToggleComplete={handleToggleComplete}
          />
        </div>
      </main>
    </div>
  );
}
