import React, { useEffect, useState } from "react";
import Header from "./components/Header";
import TaskForm from "./components/TaskForm";
import TaskList from "./components/TaskList";
import Dashboard from "./components/Dashboard";
import { getTasks, createTask, updateTask, deleteTask } from "./api";
import Confetti from "react-confetti";
import "./App.css";

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [editing, setEditing] = useState(null);
  const [coins, setCoins] = useState(0);
  const [showConfetti, setShowConfetti] = useState(false);
  const [message, setMessage] = useState("");
  const [filter, setFilter] = useState("All");
  const [selectedDate, setSelectedDate] = useState(new Date());

  const fetchTasks = async () => {
    try {
      const res = await getTasks();
      setTasks(res.data);
      const completed = res.data.filter((t) => t.completed).length;
      setCoins(completed * 10);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleCreate = async (payload) => {
    await createTask(payload);
    fetchTasks();
  };

  const handleUpdate = async (id, payload) => {
    await updateTask(id, payload);
    setEditing(null);
    fetchTasks();
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this task?")) return;
    await deleteTask(id);
    fetchTasks();
  };

  const handleToggleComplete = async (task) => {
    const updated = { ...task, completed: !task.completed };
    await handleUpdate(task._id, updated);
    if (updated.completed) {
      setShowConfetti(true);
      setMessage(
        `ðŸŽ‰ Congratulations! You earned 10 coins for completing "${task.title}"!`
      );
      setTimeout(() => {
        setShowConfetti(false);
        setMessage("");
      }, 3000);
    }
  };

  const filteredTasks = tasks.filter((t) => {
    if (filter === "Pending") return !t.completed;
    if (filter === "Completed") return t.completed;
    if (filter === "Today")
      return t.dueDate?.split("T")[0] === selectedDate.toISOString().split("T")[0];
    return true;
  });

  return (
    <div className="container">
      {showConfetti && <Confetti />}
      <Header coins={coins} />
      {message && <div className="motivation">{message}</div>}
      <main>
        <div className="left">
          <TaskForm
            onCreate={handleCreate}
            onUpdate={handleUpdate}
            editing={editing}
            cancelEdit={() => setEditing(null)}
          />
          <div className="filters">
            {["All", "Pending", "Completed", "Today"].map((f) => (
              <button
                key={f}
                className={filter === f ? "active" : ""}
                onClick={() => setFilter(f)}
              >
                {f}
              </button>
            ))}
          </div>
          <TaskList
            tasks={filteredTasks}
            onEdit={(t) => setEditing(t)}
            onDelete={handleDelete}
            onToggleComplete={handleToggleComplete}
            selectedDate={selectedDate}
            setSelectedDate={setSelectedDate}
          />
        </div>

        <div className="right">
          <Dashboard tasks={tasks} />
        </div>
      </main>
    </div>
  );
}
