import React from 'react';
import './TaskList.css';

export default function TaskList({ tasks, onEdit, onDelete, onToggleComplete }) {
  return (
    <div className="task-list glass">
      <h2>Your Tasks</h2>
      {tasks.length === 0 && <p>No tasks yet. Add one!</p>}
      {tasks.map((task) => (
        <div key={task._id} className={`task-card ${task.completed ? 'done' : ''}`}>
          <h3>{task.title}</h3>
          <p><b>Subject:</b> {task.subject}</p>
          <p>{task.description}</p>
          <p><b>Due:</b> {task.dueDate?.slice(0, 10)}</p>

          <div className="btn-group">
            {!task.completed ? (
              <button onClick={() => onToggleComplete(task)}>‚úÖ Complete</button>
            ) : (
              <button onClick={() => onToggleComplete(task)}>‚Ü©Ô∏è Undo</button>
            )}
            <button className="edit" onClick={() => onEdit(task)}>‚úèÔ∏è Edit</button>
            <button className="delete" onClick={() => onDelete(task._id)}>üóëÔ∏è Delete</button>
          </div>
        </div>
      ))}
    </div>
  );
}
