import React, { useEffect, useState } from 'react';
import Header from './components/Header';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import Dashboard from './components/Dashboard';
import { getTasks, createTask, updateTask, deleteTask } from './api';

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [editing, setEditing] = useState(null);
  const fetchTasks = async () => {
    try {
      const res = await getTasks();
      setTasks(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(()=> { fetchTasks(); }, []);

  const handleCreate = async (payload) => {
    try {
      await createTask(payload);
      fetchTasks();
    } catch (err) { console.error(err); }
  };

  const handleUpdate = async (id, payload) => {
    try {
      await updateTask(id, payload);
      setEditing(null);
      fetchTasks();
    } catch (err) { console.error(err); }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this task?')) return;
    try {
      await deleteTask(id);
      fetchTasks();
    } catch (err) { console.error(err); }
  };

  return (
    <div className="container">
      <Header />
      <main>
        <div className="left">
          <TaskForm onCreate={handleCreate} onUpdate={handleUpdate} editing={editing} cancelEdit={()=>setEditing(null)} />
          <Dashboard tasks={tasks} />
        </div>
        <div className="right">
          <TaskList tasks={tasks} onEdit={(t)=>setEditing(t)} onDelete={handleDelete} onToggleComplete={async (t)=> {
            await handleUpdate(t._id, { ...t, completed: !t.completed });
          }} />
        </div>
      </main>
    </div>
  );
}
