import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip } from 'chart.js';
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip);

export default function Dashboard({ tasks }) {
  const subjects = {};
  tasks.forEach(t => {
    const s = t.subject || 'General';
    subjects[s] = (subjects[s] || 0) + 1;
  });

  const labels = Object.keys(subjects);
  const data = {
    labels,
    datasets: [{ label: 'Tasks per subject', data: labels.map(l=>subjects[l]), barPercentage: 0.6 }]
  };

  const completed = tasks.filter(t=>t.completed).length;
  const total = tasks.length;
  const progress = total ? Math.round((completed/total)*100) : 0;

  return (
    <aside className="dashboard">
      <h3>Dashboard</h3>
      <div className="stats">
        <div className="stat"><strong>{total}</strong><span>Total</span></div>
        <div className="stat"><strong>{completed}</strong><span>Completed</span></div>
        <div className="stat"><strong>{progress}%</strong><span>Progress</span></div>
      </div>
      <div className="chart">
        <Bar data={data} options={{ maintainAspectRatio: false, plugins: { title: { display: true, text: 'Tasks by Subject' } } }} />
      </div>
    </aside>
  );
}
