import React, { useState, useEffect } from "react";
import "./TaskList.css";
import { Pie } from "react-chartjs-2";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

export default function TaskList({ tasks, onEdit, onDelete, onToggleComplete }) {
  const [coins, setCoins] = useState(0);
  const [value, setValue] = useState(new Date());
  const [tasksOnDate, setTasksOnDate] = useState([]);
  const [hoveredDate, setHoveredDate] = useState(null);

  // Update coins
  useEffect(() => {
    const completed = tasks.filter((t) => t.completed).length;
    setCoins(completed * 5); // 5 coins per completed task
  }, [tasks]);

  // Update tasks on selected date
  useEffect(() => {
    const filtered = tasks.filter(
      (t) => new Date(t.dueDate).toDateString() === value.toDateString()
    );
    setTasksOnDate(filtered);
  }, [value, tasks]);

  const completedTasks = tasks.filter((t) => t.completed).length;
  const totalTasks = tasks.length;
  const completionPercent = totalTasks ? (completedTasks / totalTasks) * 100 : 0;

  const pieData = {
    labels: ["Completed", "Remaining"],
    datasets: [
      {
        data: [completedTasks, totalTasks - completedTasks],
        backgroundColor: ["#4CAF50", "#FFC107"],
        borderWidth: 2,
      },
    ],
  };

  const renderTileContent = ({ date, view }) => {
    if (view === "month") {
      const tasksDue = tasks.filter(
        (t) => new Date(t.dueDate).toDateString() === date.toDateString()
      );
      return tasksDue.length > 0 ? (
        <div className="calendar-dot-container">
          <div className="calendar-dot"></div>
          {/* Tooltip */}
          <div className="tooltip">
            {tasksDue.map((t) => (
              <div key={t._id}>
                <strong>{t.title}</strong> ({t.subject})
              </div>
            ))}
          </div>
        </div>
      ) : null;
    }
  };

  const formatTaskCardClass = (task) => {
    if (task.completed) return "task-card completed";
    if (new Date(task.dueDate) < new Date()) return "task-card overdue";
    return "task-card";
  };

  return (
    <div className="tasklist-container">
      {/* Left: Task list */}
      <div className="tasklist-left">
        <h2>üìò Task List</h2>
        {tasks.length === 0 ? (
          <p className="empty">No tasks added yet!</p>
        ) : (
          tasks.map((task) => (
            <div key={task._id} className={formatTaskCardClass(task)}>
              <div className="task-info">
                <h3>
                  {task.title}
                  {task.priority && (
                    <span className={`priority ${task.priority}`}>{task.priority}</span>
                  )}
                </h3>
                <p><strong>Subject:</strong> {task.subject}</p>
                <p>{task.description}</p>
                <p><strong>Due:</strong> {task.dueDate?.split("T")[0]}</p>
              </div>
              <div className="task-actions">
                <button onClick={() => onToggleComplete(task)}>
                  {task.completed ? "Undo" : "Complete"}
                </button>
                <button onClick={() => onEdit(task)} className="edit">Edit</button>
                <button onClick={() => onDelete(task)} className="delete">Delete</button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Right: Dashboard */}
      <div className="tasklist-right">
        <div className="pie-section">
          <h3>Progress Overview</h3>
          <Pie data={pieData} />
          <p>{Math.round(completionPercent)}% Completed</p>
        </div>

        <div className="coins-section">
          <h3>üèÖ Coins Earned</h3>
          <div className="coins-display">{coins}</div>
          <p>Keep going! More completed tasks = more coins üéâ</p>
        </div>

        <div className="calendar-section">
          <h3>üìÖ Task Calendar</h3>
          <Calendar
            onChange={setValue}
            value={value}
            tileContent={renderTileContent}
          />

          {/* Selected date tasks */}
          {tasksOnDate.length > 0 && (
            <div className="tasks-popup">
              <h4>Tasks on {value.toDateString()}:</h4>
              {tasksOnDate.map((task) => (
                <div key={task._id} className="popup-task">
                  <strong>{task.title}</strong> - {task.subject}
                  {task.priority && (
                    <span className={`priority ${task.priority}`}>{task.priority}</span>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
