import React from 'react';
import TaskCard from './TaskCard';

export default function TaskList({ tasks, onEdit, onDelete, onToggleComplete }) {
  if (!tasks.length) return <div className="no-tasks">No tasks created yet.</div>;

  const grouped = tasks.reduce((acc, t) => {
    const sub = t.subject || 'General';
    (acc[sub] = acc[sub] || []).push(t);
    return acc;
  }, {});

  return (
    <div className="task-list">
      {Object.keys(grouped).map(sub => (
        <section key={sub}>
          <h3 className="subject-title">{sub}</h3>
          <div className="cards">
            {grouped[sub].map(t => (
              <TaskCard key={t._id} task={t} onEdit={onEdit} onDelete={onDelete} onToggleComplete={onToggleComplete} />
            ))}
          </div>
        </section>
      ))}
    </div>
  );
}
