import React, { useEffect, useState } from "react";
import { getTasks, createTask, updateTask, deleteTask } from "./api";
import Confetti from "react-confetti";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [subject, setSubject] = useState("");
  const [description, setDescription] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [sessions, setSessions] = useState(1);
  const [coins, setCoins] = useState(0);
  const [showConfetti, setShowConfetti] = useState(false);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    const res = await getTasks();
    setTasks(res.data);
  };

  const addTask = async () => {
    if (!title) return;
    await createTask({ title, subject, description, dueDate, sessions });
    setTitle("");
    setSubject("");
    setDescription("");
    setDueDate("");
    setSessions(1);
    fetchTasks();
  };

  const markComplete = async (id) => {
    await updateTask(id, { completed: true });
    setShowConfetti(true);
    setCoins(coins + 10);
    setTimeout(() => setShowConfetti(false), 3000);
    fetchTasks();
  };

  const removeTask = async (id) => {
    await deleteTask(id);
    fetchTasks();
  };

  return (
    <div className="main-container">
      {showConfetti && <Confetti />}
      <header>
        <h1>üéì StudyBuddy</h1>
        <p>Plan your study, earn rewards, and stay motivated!</p>
        <div className="coin-box">üí∞ Coins: {coins}</div>
      </header>

      <div className="content">
        <div className="task-form glass">
          <h2>Add Task</h2>
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <input
            type="text"
            placeholder="Subject (e.g., Math)"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
          />
          <textarea
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <input
            type="date"
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
          />
          <input
            type="number"
            min="1"
            value={sessions}
            onChange={(e) => setSessions(e.target.value)}
          />
          <button onClick={addTask}>Create Task</button>
        </div>

        <div className="task-list glass">
          <h2>Your Tasks</h2>
          {tasks.map((task) => (
            <div
              key={task._id}
              className={`task-card ${task.completed ? "done" : ""}`}
            >
              <h3>{task.title}</h3>
              <p><b>Subject:</b> {task.subject}</p>
              <p>{task.description}</p>
              <p><b>Due:</b> {task.dueDate?.slice(0, 10)}</p>
              {!task.completed ? (
                <button onClick={() => markComplete(task._id)}>‚úÖ Complete</button>
              ) : (
                <span className="badge">üéâ Completed!</span>
              )}
              <button className="delete" onClick={() => removeTask(task._id)}>üóëÔ∏è Delete</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
