import React from 'react';

export default function TaskCard({ task, onEdit, onDelete, onToggleComplete }) {
  const due = task.dueDate ? new Date(task.dueDate).toLocaleDateString() : '‚Äî';
  return (
    <div className={`task-card ${task.completed ? 'done' : ''}`}>
      <div className="card-top">
        <h4>{task.title}</h4>
        <div className="meta">
          <span className="pom">üçÖ {task.pomodoroEstimate}</span>
          <span className="due">Due: {due}</span>
        </div>
      </div>
      <p className="desc">{task.description}</p>
      <div className="card-actions">
        <button onClick={()=>onToggleComplete(task)}>{task.completed ? 'Undo' : 'Done'}</button>
        <button onClick={()=>onEdit(task)}>Edit</button>
        <button onClick={()=>onDelete(task._id)} className="danger">Delete</button>
      </div>
    </div>
  );
}
